function loadVoices() {
    availableVoices = window.speechSynthesis.getVoices();
    
    // Find best available voice
    if (!preferredVoice && availableVoices.length > 0) {
        // First try premium voices
        for (var i = 0; i < premiumVoiceNames.length; i++) {
            for (var j = 0; j < availableVoices.length; j++) {
                if (availableVoices[j].name.indexOf(premiumVoiceNames[i]) >= 0) {
                    preferredVoice = availableVoices[j];
                    console.log('Selected premium voice:', preferredVoice.name);
                    return;
                }
            }
        }
        
        // Fallback: find any good English voice
        for (var j = 0; j < availableVoices.length; j++) {
            var v = availableVoices[j];
            if (v.lang.startsWith('en') && v.localService === false) {
                // Prefer network/cloud voices (usually better quality)
                preferredVoice = v;
                console.log('Selected network voice:', preferredVoice.name);
                return;
            }
        }
        
        // Final fallback: any English voice
        for (var j = 0; j < availableVoices.length; j++) {
            if (availableVoices[j].lang.startsWith('en')) {
                preferredVoice = availableVoices[j];
                console.log('Selected fallback voice:', preferredVoice.name);
                return;
            }
        }
    }
}
