    for (var i = 0; i < words.length; i++) {
        var cleanWord = words[i].replace(/[.,!?;:'"-]/g, '');
        var punct = words[i].replace(cleanWord, '');
        sentenceHtml += '<span class="highlight-word" data-idx="' + i + '" onclick="toggleWordHighlight(' + i + ')">' + cleanWord + '</span>';
        if (punct) sentenceHtml += '<span class="punct">' + punct + '</span>';
    }
    sentenceHtml += '</div>';
    
    // Build selection counter
    var totalNeeded = 0;
    for (var pos in targets) totalNeeded += targets[pos];
    
    var html = '<div class="highlight-instructions">' + instructions + '</div>' +
               '<div class="highlight-sentence-container">' + sentenceHtml + '</div>' +
               '<div class="highlight-counter" id="highlightCounter">Selected: 0 of ' + totalNeeded + '</div>' +
               '<div class="pos-selector-container">' +
               '<div class="pos-status" id="posStatus">First, select a part of speech below. Then tap words in the sentence.</div>' +
               '<div class="pos-buttons">';
    
    // Only show buttons for POS types we're asking for
    if (targets.noun) html += '<button class="pos-btn noun-btn" onclick="selectPOS(\'noun\')">üìò Noun</button>';
    if (targets.verb) html += '<button class="pos-btn verb-btn" onclick="selectPOS(\'verb\')">üèÉ Verb</button>';
    if (targets.adjective) html += '<button class="pos-btn adjective-btn" onclick="selectPOS(\'adjective\')">‚ú® Adjective</button>';
    if (targets.adverb) html += '<button class="pos-btn adverb-btn" onclick="selectPOS(\'adverb\')">‚ö° Adverb</button>';
    
    html += '</div>' +
            '<div class="pos-action-btns">' +
            '<button class="pos-action-btn read-btn" onclick="speakHighlightSentence()">üîä Read Sentence</button>' +
            '<button class="pos-action-btn check-btn" id="posCheckBtn" onclick="checkHighlightAnswer()" disabled>‚úì Check Answers</button>' +
            '</div></div>';
    
    area.innerHTML = html;
    
