    var ht = document.getElementById('hintText');
    var fb = document.getElementById('feedbackBox');
    var nb = document.getElementById('nextBtn');
    
    if (hb) hb.style.display = 'none';
    if (ht) ht.textContent = q.hint || 'Think about the parts of speech!';
    if (fb) { fb.style.display = 'none'; fb.className = 'feedback-box'; }
    if (nb) nb.classList.add('hidden');
    
    // Update progress
    var pct = (questionIndex / currentQuestions.length) * 100;
    var pf = document.getElementById('progressFill');
