function loadAchievements() {
    var saved = localStorage.getItem('readingQuestAchievements');
    if (saved) {
        try {
            var data = JSON.parse(saved);
            if (data.achievements) {
                for (var key in data.achievements) {
                    if (achievements[key]) {
                        achievements[key].earned = data.achievements[key].earned;
                    }
                }
            }
            if (data.stats) {
                achievementStats = Object.assign(achievementStats, data.stats);
            }
        } catch(e) { console.log('Error loading achievements'); }
    }
}

function saveAchievements() {
    localStorage.setItem('readingQuestAchievements', JSON.stringify({
        achievements: achievements,
        stats: achievementStats
    }));
}

