// Function that ONLY returns words actually in the story
function getStoryVocabulary(storyText, storyLevel, storyId) {
    // First check if story has explicit vocabulary array
    var story = stories.find(function(s) { return s.id === storyId; });
    
    if (story && story.vocabulary && story.vocabulary.length > 0) {
        // Use story's predefined vocabulary
        var found = [];
        story.vocabulary.forEach(function(word) {
            var wordLower = word.toLowerCase();
            if (storyVocabDB[wordLower]) {
                found.push({
                    word: wordLower,
                    def: storyVocabDB[wordLower].def,
                    pos: storyVocabDB[wordLower].pos,
                    length: wordLower.length
                });
            } else {
                // Skip words not in DB ‚Äî generic definitions break the quiz
            }
        });
        return found.slice(0, 5);
    }
    
    // Fallback: Get unique words from THIS story
    var storyWordsRaw = storyText.toLowerCase().match(/[a-z]+/g) || [];
    var storyWords = {};
    storyWordsRaw.forEach(function(w) { storyWords[w] = true; });
    
    // Find vocab words that ARE in this story
    var found = [];
    
    for (var word in storyVocabDB) {
        if (storyWords[word]) {
            found.push({
                word: word,
                def: storyVocabDB[word].def,
                pos: storyVocabDB[word].pos,
                length: word.length
            });
        }
    }
    
    // Sort by word length (longer = harder)
    found.sort(function(a, b) {
        return b.length - a.length;
    });
    
    // Return 3-5 of the hardest words
    var count = Math.min(5, found.length);
    return found.slice(0, count);
}




