function setupSlider() {
    var track = document.getElementById('sliderTrack');
    var minH = document.getElementById('minHandle');
    var maxH = document.getElementById('maxHandle');
    
    if (!track || !minH || !maxH) return;
    
    minH.onmousedown = function(e) { e.preventDefault(); dragging = true; dragHandle = 'min'; };
    maxH.onmousedown = function(e) { e.preventDefault(); dragging = true; dragHandle = 'max'; };
    
    document.onmousemove = function(e) {
        if (!dragging) return;
        var rect = track.getBoundingClientRect();
        var pct = Math.max(0, Math.min(1, (e.clientX - rect.left) / rect.width));
        var lvl = Math.round(pct * (LEVELS.length - 1));
        if (dragHandle === 'min') minLevel = Math.min(lvl, maxLevel);
        else if (dragHandle === 'max') maxLevel = Math.max(lvl, minLevel);
        updateSliderUI();
    };
    
    document.onmouseup = function() { dragging = false; dragHandle = null; };
    
    minH.ontouchstart = function(e) { e.preventDefault(); dragging = true; dragHandle = 'min'; };
    maxH.ontouchstart = function(e) { e.preventDefault(); dragging = true; dragHandle = 'max'; };
    
    document.ontouchmove = function(e) {
        if (!dragging || !e.touches || !e.touches[0]) return;
        var rect = track.getBoundingClientRect();
        var pct = Math.max(0, Math.min(1, (e.touches[0].clientX - rect.left) / rect.width));
        var lvl = Math.round(pct * (LEVELS.length - 1));
        if (dragHandle === 'min') minLevel = Math.min(lvl, maxLevel);
        else if (dragHandle === 'max') maxLevel = Math.max(lvl, minLevel);
        updateSliderUI();
    };
    
    document.ontouchend = function() { dragging = false; dragHandle = null; };
    
    track.onclick = function(e) {
        if (e.target.id === 'minHandle' || e.target.id === 'maxHandle') return;
        var rect = track.getBoundingClientRect();
        var pct = (e.clientX - rect.left) / rect.width;
        var lvl = Math.round(pct * (LEVELS.length - 1));
        lvl = Math.max(0, Math.min(LEVELS.length - 1, lvl));
        if (Math.abs(lvl - minLevel) <= Math.abs(lvl - maxLevel)) {
            if (lvl <= maxLevel) minLevel = lvl;
        } else {
            if (lvl >= minLevel) maxLevel = lvl;
        }
        updateSliderUI();
    };
    
    updateSliderUI();
}

function updateSliderUI() {
    var range = document.getElementById('sliderRange');
    var minH = document.getElementById('minHandle');
    var maxH = document.getElementById('maxHandle');
    if (!range || !minH || !maxH) return;
    
    var minPct = (minLevel / (LEVELS.length - 1)) * 100;
    var maxPct = (maxLevel / (LEVELS.length - 1)) * 100;
    
    range.style.left = minPct + '%';
    range.style.width = (maxPct - minPct) + '%';
    minH.style.left = minPct + '%';
    maxH.style.left = maxPct + '%';
    minH.textContent = LEVELS[minLevel];
    maxH.textContent = LEVELS[maxLevel];
    
    var r = document.getElementById('rangeDisplay');
    var c = document.getElementById('levelCount');
    if (r) r.textContent = LEVELS[minLevel] + ' - ' + LEVELS[maxLevel];
    if (c) c.textContent = (maxLevel - minLevel + 1);
    
    updateStoryCount();
    
    var labels = document.querySelectorAll('.level-label');
    for (var i = 0; i < labels.length; i++) {
        if (i >= minLevel && i <= maxLevel) labels[i].classList.add('active');
        else labels[i].classList.remove('active');
    }
}

function updateStoryCount() {
    var valid = LEVELS.slice(minLevel, maxLevel + 1);
    var count = 0;
    for (var i = 0; i < stories.length; i++) {
        var s = stories[i];
        if (valid.indexOf(s.level) < 0) continue;
        if (selectedGenre !== 'both' && s.genre !== selectedGenre) continue;
        count++;
    }
    var e1 = document.getElementById('storyCount');
    var e2 = document.getElementById('storyCountNumber');
    if (e1) e1.textContent = count;
    if (e2) e2.textContent = count;
    renderStoryProgress();
}

