function startPractice() {
    // Initialize activity log for this session
    initSessionLog();
    
    questionIndex = 0;
    correctCount = 0;
    hintCount = 0;
    hasAnswered = false;
    selectedAnswerIndex = null;
    
    // Reset reading attempts for new practice session
    resetReadingAttempts();
    
    var valid = LEVELS.slice(minLevel, maxLevel + 1);
    
    // Step 1: Find all valid stories
    var validStories = [];
    for (var i = 0; i < stories.length; i++) {
        var s = stories[i];
        if (valid.indexOf(s.level) < 0) continue;
        if (selectedGenre !== 'both' && s.genre !== selectedGenre) continue;
        validStories.push(s);
    }
    
    if (validStories.length === 0) { 
        alert('No stories found for selected criteria.'); 
        return; 
    }
    
    // Step 2: Pick a random story (prefer unfinished)
    var unfinished = [];
    for (var i = 0; i < validStories.length; i++) {
        if (getStoryStatus(validStories[i].id) !== 'passed') {
            unfinished.push(validStories[i]);
        }
    }
    var pool = unfinished.length > 0 ? unfinished : validStories;
    var randomIndex = Math.floor(Math.random() * pool.length);
    var selectedStory = pool[randomIndex];
    currentStoryId = selectedStory.id;
    console.log('DEBUG: Selected story ID:', currentStoryId, 'Level:', selectedStory.level, 'Title:', selectedStory.title);
    
    // Start logging this story
    startStoryLog(selectedStory.id, selectedStory.title, selectedStory.level, selectedStory.wordCount);
    
    // Step 3: Get all questions for this story
    var storyQuestions = [];
    console.log('DEBUG: Searching through', questions.length, 'questions for storyId', currentStoryId);
    for (var i = 0; i < questions.length; i++) {
        var q = questions[i];
        if (!q || typeof q !== 'object') {
            console.log('DEBUG: Skipping invalid question at index', i);
            continue;
        }
        if (q.storyId === undefined) {
            console.log('DEBUG: Question at index', i, 'has no storyId:', q);
            continue;
        }
        if (q.storyId === currentStoryId) {
            // If category filter is set, apply it
            if (selectedCategory === 'mixed' || q.category === selectedCategory) {
                storyQuestions.push(q);
            }
        }
    }
    
    console.log('DEBUG: Found', storyQuestions.length, 'questions for story', currentStoryId, 'with category', selectedCategory);
    if (storyQuestions.length === 0) {
        console.log('DEBUG: No questions found. Checking questions array...');
        console.log('DEBUG: Total questions in array:', questions.length);
        var anyForStory = questions.filter(function(q) { return q && q.storyId === currentStoryId; });
        console.log('DEBUG: Questions matching storyId', currentStoryId, ':', anyForStory.length);
        if (anyForStory.length > 0) {
            console.log('DEBUG: Sample question:', anyForStory[0]);
        }
        alert('No questions found for this story (ID: ' + currentStoryId + '). Try different settings.');
        return;
    }
    
    // Step 4: Shuffle and take up to 6 questions
    for (var i = storyQuestions.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var t = storyQuestions[i]; 
        storyQuestions[i] = storyQuestions[j]; 
        storyQuestions[j] = t;
    }
    
    currentQuestions = storyQuestions.slice(0, 6);
    currentStoryQuestions = currentQuestions.slice(); // Keep a copy
    
    // Start focus monitoring
    if (typeof FocusMonitor !== 'undefined') FocusMonitor.start();

    // Show vocabulary pre-teach before starting
    showVocabularyPreTeach(selectedStory, function() {
        document.getElementById('homeScreen').classList.add('hidden');
        document.getElementById('resultsScreen').classList.add('hidden');
        document.getElementById('practiceScreen').classList.remove('hidden');
        displayQuestion();
    });
}

function displayQuestion() {
    if (questionIndex >= currentQuestions.length) { showResults(); return; }
    
    // Ensure all audio buttons are in default state
    var speakBtn = document.getElementById('speakBtn');
    var stopBtn = document.getElementById('stopSpeakBtn');
    var pauseBtn = document.getElementById('pauseBtn');
    var highlightBtn = document.getElementById('highlightReadBtn');
    if (speakBtn) speakBtn.classList.remove('hidden');
    if (highlightBtn) highlightBtn.classList.remove('hidden');
    if (stopBtn) stopBtn.classList.add('hidden');
    if (pauseBtn) pauseBtn.classList.add('hidden');
    
    // Stop any ongoing speech
    if (window.speechSynthesis) window.speechSynthesis.cancel();
    isHighlightReading = false;
    isSpeaking = false;
    
    // Start timing this question
    startQuestionTimer();
    
    hasAnswered = false;
    selectedAnswerIndex = null;
    
    var q = currentQuestions[questionIndex];
    
    // Safety check - if question is undefined, show error and return
    if (!q) {
        console.error('Question not found at index:', questionIndex, 'currentQuestions:', currentQuestions);
        alert('Error loading question. Please try again.');
        goHome();
        return;
    }
    
    var story = null;
    for (var i = 0; i < stories.length; i++) {
        if (stories[i].id === q.storyId) { story = stories[i]; break; }
    }
    
    // Set word count for WPM calculation
    if (story) {
        currentWordCount = story.wordCount || story.text.split(/\s+/).length;
    }

    // Set context-aware idle threshold based on current phase
    if (typeof FocusMonitor !== 'undefined' && FocusMonitor.setIdleThreshold) {
        if (questionsUnlocked) {
            FocusMonitor.setIdleThreshold(30);
        } else {
            FocusMonitor.setIdleThreshold(currentWordCount <= 100 ? 60 : 90);
        }
    }

    // Reading attempts are now managed per story in startPractice
    // No need to reset here since all questions are from the same story
    
    var t = document.getElementById('storyTitle');
    var x = document.getElementById('storyText');
    if (t) t.innerHTML = '<span>' + (story ? story.title : '') + '</span>';
    if (x) x.textContent = story ? story.text : '';
    
    var qn = document.getElementById('questionNumber');
    var qb = document.getElementById('questionBadge');
    var qt = document.getElementById('questionTextContent') || document.getElementById('questionText');
    if (qn) qn.textContent = 'Question ' + (questionIndex + 1);
    if (qb) qb.textContent = q.category;
    if (qt) qt.textContent = q.question;
    
    var area = document.getElementById('answerArea');
    if (area) {
        var html = '';
        var letters = ['A', 'B', 'C', 'D', 'E', 'F'];
        var displayOptions = [];
        var correctIndex = 0;
        
        if (q.type === 'truefalse') {
            // True/False - randomly swap positions
            var tfFirst = Math.random() < 0.5;
            currentAnswerMap = tfFirst ? [0, 1] : [1, 0];
            var opts = tfFirst ? ['True', 'False'] : ['False', 'True'];
            var syms = tfFirst ? ['‚úì', '‚úó'] : ['‚úó', '‚úì'];
            html = '<div class="tf-options-container">' +
                   '<button class="answer-option tf-option" onclick="pickAnswer(0)">' +
                   '<span class="option-letter">' + syms[0] + '</span>' +
                   '<span class="option-text">' + opts[0] + '</span>' +
                   '<span class="speak-option-btn" onclick="event.stopPropagation(); speakText(\'' + opts[0] + '\', this);" title="Listen">üîä</span></button>' +
                   '<button class="answer-option tf-option" onclick="pickAnswer(1)">' +
                   '<span class="option-letter">' + syms[1] + '</span>' +
                   '<span class="option-text">' + opts[1] + '</span>' +
                   '<span class="speak-option-btn" onclick="event.stopPropagation(); speakText(\'' + opts[1] + '\', this);" title="Listen">üîä</span></button></div>';
        } else if (q.type === 'sequence' && q.items) {
            // Sequence question - convert to multiple choice with ordering options
            // If no correct array, assume items are already in correct order
            var correctOrder = q.correct;
            if (!correctOrder || correctOrder.length === 0) {
                correctOrder = [];
                for (var idx = 0; idx < q.items.length; idx++) correctOrder.push(idx);
            }
            var correctText = '';
            for (var i = 0; i < correctOrder.length; i++) {
                correctText += (i + 1) + '. ' + q.items[correctOrder[i]];
                if (i < correctOrder.length - 1) correctText += '\n';
            }
            displayOptions.push(correctText);
            correctIndex = 0;
            
            // Generate wrong orderings
            var wrongOrders = [];
            for (var attempt = 0; attempt < 10 && wrongOrders.length < 3; attempt++) {
                var shuffled = correctOrder.slice();
                for (var i = shuffled.length - 1; i > 0; i--) {
                    var j = Math.floor(Math.random() * (i + 1));
                    var t = shuffled[i]; shuffled[i] = shuffled[j]; shuffled[j] = t;
                }
                var wrongText = '';
                for (var i = 0; i < shuffled.length; i++) {
                    wrongText += (i + 1) + '. ' + q.items[shuffled[i]];
                    if (i < shuffled.length - 1) wrongText += '\n';
                }
                if (wrongText !== correctText && wrongOrders.indexOf(wrongText) === -1) {
                    wrongOrders.push(wrongText);
                }
            }
            for (var i = 0; i < wrongOrders.length; i++) {
                displayOptions.push(wrongOrders[i]);
            }
            q.correct = 0; // The correct answer is now at index 0 before shuffle
            
        } else if (q.type === 'fillblank') {
            // Fill in blank - use words or options as the choices
            if (q.words && q.words.length > 0) {
                displayOptions = q.words.slice();
                for (var i = 0; i < displayOptions.length; i++) {
                    if (displayOptions[i] === q.correct) {
                        correctIndex = i;
                        break;
                    }
                }
                q.correct = correctIndex;
            } else if (q.options && q.options.length > 0) {
                displayOptions = q.options.slice();
                correctIndex = q.correct || 0;
            }
            
        } else if (q.type === 'matching' && q.pairs) {
            // Matching - show correct matches as one option, wrong matches as others
            var correctMatch = '';
            for (var i = 0; i < q.pairs.length; i++) {
                correctMatch += q.pairs[i][0] + ' ‚Üí ' + q.pairs[i][1];
                if (i < q.pairs.length - 1) correctMatch += '\n';
            }
            displayOptions.push(correctMatch);
            correctIndex = 0;
            
            // Generate wrong matches by shuffling second elements
            for (var w = 0; w < 3; w++) {
                var shuffledPairs = [];
                var seconds = [];
                for (var i = 0; i < q.pairs.length; i++) seconds.push(q.pairs[i][1]);
                // Shuffle seconds
                for (var i = seconds.length - 1; i > 0; i--) {
                    var j = Math.floor(Math.random() * (i + 1));
                    var t = seconds[i]; seconds[i] = seconds[j]; seconds[j] = t;
                }
                var wrongMatch = '';
                for (var i = 0; i < q.pairs.length; i++) {
                    wrongMatch += q.pairs[i][0] + ' ‚Üí ' + seconds[i];
                    if (i < q.pairs.length - 1) wrongMatch += '\n';
                }
                if (wrongMatch !== correctMatch && displayOptions.indexOf(wrongMatch) === -1) {
                    displayOptions.push(wrongMatch);
                }
            }
            q.correct = 0;
            
        } else if (q.type === 'highlight' && q.sentence) {
            // Parts of speech game - determine mode from story level
            var storyLevel = story ? story.level : 'A';
            var gameMode = getPOSGameMode(storyLevel);
            if (gameMode === 'simple') {
                renderSimplePOSGame(q, area, storyLevel);
            } else {
                renderAdvancedPOSGame(q, area, storyLevel);
            }
            return; // Don't process further for highlight questions
            
        } else if (q.options) {
            // Standard multiple choice
            displayOptions = q.options.slice();
            correctIndex = q.correct;
        }
        
        // Now render the options (for non-truefalse questions)
        if (q.type !== 'truefalse' && displayOptions.length > 0) {
            // Shuffle display options
            var indices = [];
            for (var i = 0; i < displayOptions.length; i++) indices.push(i);
            for (var i = indices.length - 1; i > 0; i--) {
                var j = Math.floor(Math.random() * (i + 1));
                var t = indices[i]; indices[i] = indices[j]; indices[j] = t;
            }
            currentAnswerMap = indices;
            
            // For sequence/matching, update q.correct to reflect original correct position
            if (q.type === 'sequence' || q.type === 'matching' || q.type === 'fillblank') {
                q.correct = correctIndex;
            }
            
            html = '<div class="answer-options-container">';
            for (var i = 0; i < indices.length; i++) {
                var origIdx = indices[i];
                var optText = displayOptions[origIdx].replace(/'/g, "'").replace(/\n/g, ' ');
                var displayText = displayOptions[origIdx].replace(/\n/g, '<br>');
                html += '<button class="answer-option" onclick="pickAnswer(' + i + ')">' +
                        '<span class="option-letter">' + letters[i] + '</span>' +
                        '<span class="option-text">' + displayText + '</span>' +
                        '<span class="speak-option-btn" onclick="event.stopPropagation(); speakText(\'' + optText + '\', this);" title="Listen">üîä</span></button>';
            }
            html += '</div>';
        }
        area.innerHTML = html;
    }
    
    var hb = document.getElementById('hintBox');
    var ht = document.getElementById('hintText');
    var fb = document.getElementById('feedbackBox');
    var cb = document.getElementById('checkBtn');
    var nb = document.getElementById('nextBtn');
    var qc = document.getElementById('questionCard');
    
    // Handle locked state
    if (!questionsUnlocked) {
        if (qc) {
            qc.classList.add('locked');
            qc.style.opacity = '0.5';
            qc.style.pointerEvents = 'none';
            
            // Add or update locked overlay
            var overlay = document.getElementById('lockedOverlay');
            if (!overlay) {
                overlay = document.createElement('div');
                overlay.id = 'lockedOverlay';
                overlay.className = 'locked-overlay';
                overlay.innerHTML = '<div class="locked-message"><span class="icon">üîí</span>Complete 3 reading attempts to unlock!</div>';
                qc.style.position = 'relative';
                qc.appendChild(overlay);
            }
            overlay.style.display = 'flex';
        }
    } else {
        if (qc) {
            qc.classList.remove('locked');
            qc.style.opacity = '1';
            qc.style.pointerEvents = 'auto';
            var overlay = document.getElementById('lockedOverlay');
            if (overlay) overlay.style.display = 'none';
        }
    }
    
    if (hb) hb.style.display = 'none';
    if (ht) ht.textContent = q.hint || 'Think about what you read!';
    if (fb) { fb.style.display = 'none'; fb.className = 'feedback-box'; }
    if (cb) { cb.classList.remove('hidden'); cb.disabled = true; }
    if (nb) nb.classList.add('hidden');
    
    var pct = (questionIndex / currentQuestions.length) * 100;
    var pf = document.getElementById('progressFill');
    var pt = document.getElementById('progressText');
    if (pf) pf.style.width = pct + '%';
    if (pt) pt.textContent = 'Question ' + (questionIndex + 1) + ' of ' + currentQuestions.length;
}

