// Helper: determine game mode from story level
function getPOSGameMode(level) {
    var idx = LEVELS.indexOf(level);
    if (idx === -1) idx = 0;
    // aa through G (indices 0-7) = simple, H through Z (indices 8-26) = advanced
    return idx <= 7 ? 'simple' : 'advanced';
}

// Helper: determine difficulty tier from story level
function getPOSTier(level) {
    var idx = LEVELS.indexOf(level);
    if (idx === -1) idx = 0;
    if (idx <= 7) return 'early';        // aa-G
    if (idx <= 14) return 'developing';   // H-N
    return 'advanced';                     // O-Z
}

// Helper: get allowed POS types for a tier
function getAllowedPOSTypes(tier) {
    if (tier === 'early') return ['noun', 'verb'];
    if (tier === 'developing') return ['noun', 'verb', 'adjective'];
    return ['noun', 'verb', 'adjective', 'adverb'];
}

