function setupSimplePOSGame(q, tier) {
    var allowed = getAllowedPOSTypes(tier);
    posGameWords = [];
    posGameCurrentIdx = 0;
    posGameScore = 0;
    posGameMode = 'simple';
    hasAnswered = false;

    // Collect all words from q.answers that match allowed POS types
    var candidates = [];
    for (var idx in q.answers) {
        if (q.answers.hasOwnProperty(idx)) {
            var pos = q.answers[idx];
            if (allowed.indexOf(pos) !== -1) {
                candidates.push({idx: parseInt(idx), word: '', correctPOS: pos});
            }
        }
    }

    // Fill in word text from sentence
    var sentenceWords = q.sentence.replace(/[.,!?;:'"\-]/g, '').split(' ');
    for (var i = 0; i < candidates.length; i++) {
        candidates[i].word = sentenceWords[candidates[i].idx] || '';
    }

    // Determine how many words to pick based on tier
    var minWords = (tier === 'early') ? 5 : 8;
    var maxWords = (tier === 'early') ? 8 : 12;

    // Shuffle candidates and pick appropriate count
    for (var i = candidates.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = candidates[i];
        candidates[i] = candidates[j];
        candidates[j] = temp;
    }

    var pickCount = Math.min(candidates.length, maxWords);
    pickCount = Math.max(pickCount, Math.min(candidates.length, minWords));
    posGameWords = candidates.slice(0, pickCount);

    // Sort by word index so we go through the sentence in order
    posGameWords.sort(function(a, b) { return a.idx - b.idx; });

    posGameTotal = posGameWords.length;
}

function posGameHighlightTarget() {
    if (posGameCurrentIdx >= posGameWords.length) return;

    // Remove pulse from all target words
    var allTargets = document.querySelectorAll('.pos-game-target-word');
    for (var i = 0; i < allTargets.length; i++) {
        allTargets[i].classList.remove('pulse');
    }

    // Add pulse to current target
    var current = posGameWords[posGameCurrentIdx];
    var el = document.getElementById('posWord' + current.idx);
    if (el) {
        el.classList.add('pulse');
        // Scroll into view if needed
        el.scrollIntoView({behavior: 'smooth', block: 'center'});
    }
}

function posGamePick(pos) {
    if (posGameCurrentIdx >= posGameWords.length) return;

    var current = posGameWords[posGameCurrentIdx];
    var isCorrect = (pos === current.correctPOS);
    var wordEl = document.getElementById('posWord' + current.idx);
    var feedbackEl = document.getElementById('posGameFeedback');

    // Disable buttons temporarily
    var btns = document.querySelectorAll('.pos-game-pick-btn');
    for (var i = 0; i < btns.length; i++) {
        btns[i].disabled = true;
    }

    if (isCorrect) {
        posGameScore++;
        if (wordEl) {
            wordEl.classList.remove('pulse');
            wordEl.classList.add('correct-flash');
        }
        if (feedbackEl) {
            feedbackEl.innerHTML = '<span class="pos-game-correct-msg">Correct! "' + current.word + '" is a ' + current.correctPOS + '.</span>';
            feedbackEl.className = 'pos-game-feedback show correct';
        }
    } else {
        if (wordEl) {
            wordEl.classList.remove('pulse');
            wordEl.classList.add('incorrect-flash');
        }
        if (feedbackEl) {
            feedbackEl.innerHTML = '<span class="pos-game-incorrect-msg">Not quite. "' + current.word + '" is a ' + current.correctPOS + ', not a ' + pos + '.</span>';
            feedbackEl.className = 'pos-game-feedback show incorrect';
        }
    }

    // Update score display
    var scoreEl = document.getElementById('posGameScore');
    if (scoreEl) scoreEl.textContent = posGameScore;

    // Auto-advance after 1.5s
    setTimeout(function() { posGameNext(); }, 1500);
}

function posGameNext() {
    posGameCurrentIdx++;

    // Clear feedback
    var feedbackEl = document.getElementById('posGameFeedback');
    if (feedbackEl) {
        feedbackEl.innerHTML = '';
        feedbackEl.className = 'pos-game-feedback';
    }

    // Re-enable buttons
    var btns = document.querySelectorAll('.pos-game-pick-btn');
    for (var i = 0; i < btns.length; i++) {
        btns[i].disabled = false;
    }

    if (posGameCurrentIdx >= posGameWords.length) {
        posGameFinish();
        return;
    }

    // Update progress
    var progEl = document.getElementById('posGameProgress');
    if (progEl) progEl.textContent = posGameCurrentIdx + 1;

    posGameHighlightTarget();
}

function posGameFinish() {
    var q = currentQuestions[questionIndex];
    var allCorrect = (posGameScore === posGameTotal);
    hasAnswered = true;

    // Log result
    logQuestion(q.id, q.category, q.type, allCorrect);

    // Hide pick buttons
    var btnsContainer = document.querySelector('.pos-game-buttons');
    if (btnsContainer) btnsContainer.style.display = 'none';

    // Show result
    var resultEl = document.getElementById('posGameResult');
    if (resultEl) {
        var resultHtml = '<div class="pos-game-final">';
        if (allCorrect) {
            resultHtml += '<div class="pos-game-final-icon">&#10024;</div>';
            resultHtml += '<div class="pos-game-final-text">Perfect! You got ' + posGameScore + ' of ' + posGameTotal + ' correct!</div>';
            correctCount++;
            launchConfetti(30);
            updateAchievementProgress('grammarCorrect', 1);
        } else {
            resultHtml += '<div class="pos-game-final-icon">&#128218;</div>';
            resultHtml += '<div class="pos-game-final-text">You got ' + posGameScore + ' of ' + posGameTotal + ' correct. Keep practicing!</div>';
        }
        resultHtml += '</div>';
        resultEl.innerHTML = resultHtml;
    }

    // Show feedback box for consistency with rest of app
    var fb = document.getElementById('feedbackBox');
    var ft = document.getElementById('feedbackText');
    if (fb && ft) {
        fb.style.display = 'block';
        fb.classList.remove('hidden', 'correct', 'incorrect');
        fb.classList.add(allCorrect ? 'correct' : 'incorrect');
        ft.innerHTML = allCorrect
            ? '<strong>Great job!</strong> You identified all the parts of speech correctly!'
            : '<strong>' + posGameScore + ' of ' + posGameTotal + ' correct.</strong> Review the sentence and try to spot the nouns and verbs next time!';
    }

    // Show Next button
    var nb = document.getElementById('nextBtn');
    if (nb) {
        nb.classList.remove('hidden');
        nb.textContent = (questionIndex < currentQuestions.length - 1) ? 'Next Question' : 'See Results';
    }
}


